changeSet.sql:
CREATE OR REPLACE FUNCTION tsvector_manual_update_events(item_id bigint) RETURNS void LANGUAGE plpgsql AS
$$ DECLARE newtsv tsvector; sel_row sonata_events%rowtype;
BEGIN SELECT * INTO sel_row FROM sonata_events WHERE id = item_id; newtsv := setweight(to_tsvector('pg_catalog.simple', sel_row.api_used), 'A') || setweight(to_tsvector('pg_catalog.simple', sel_row.compiled_sources), 'B') || setweight(to_tsvector('pg_catalog.simple', sel_row.features_on), 'C') || setweight(to_tsvector('pg_catalog.simple', sel_row.features_off), 'C') || setweight(to_tsvector('pg_catalog.simple', sel_row.jira_case), 'D'); END $$
